                                                  <span
                                                    className="cover_icon"
                                                    onClick={(id) =>
                                                      onImgCoverEditClick(
                                                        imgg.product_image_id,
                                                        imgg.product_id,
                                                        imgg.product_verient_id
                                                      )
                                                    }
                                                  >
                                                    Set Cover
                                                  </span>
                                                  <button
                                                    className="cross_icon"
                                                    onClick={() =>
                                                      onImgRemove(
                                                        imgg.product_image_id,
                                                        imgg.product_image_name,
                                                        imgg.vendor_id,
                                                        imgg.product_id,
                                                        imgg.product_verient_id
                                                      )
                                                    }
                                                  >
                                                    &times;
                                                  </button>
                                                </div>
                                              ) : null;
                                            })}
                                            <div className="imgprivew_box">
                                              {currentPage === "choose-img" ? (

                                                <FileInput setImage={setImage} onImageSelected={onImageSelected} setimageName={setimageName} />
                                              ) : currentPage === "crop-img" ? (
                                                <ImageCropper
                                                  handleClose={handleClose}
                                                  // show={show}
                                                  image={image}
                                                  imageNamee={imageName}
                                                  modalShow={true}
                                                  onCropDone={(imgCroppedArea) => onCropDone(
                                                    imgCroppedArea,
                                                    variantdata.product_id,
                                                    variantdata.id,
                                                    variantdata.vendor_id,
                                                    console.log("variantdata.id ----onmageupdaload" + variantdata.id)



                                                  )
                                                  }
                                                  onCropCancel={onCropCancel}
                                                />
                                              ) : (
                                                <div>
                                                  <div>
                                                    <FileInput setImage={setImage} onImageSelected={onImageSelected} setimageName={setimageName} />
                                                  </div>
                                                  {/* {<FileInput/> === <ImageCropper /> ? (
                                                      <>
                                                        <button
                                                          onClick={() => {
                                                            setCurrentPage("crop-img");
                                                          }}
                                                          className="btn"
                                                        >
                                                          Crop
                                                        </button>

                                                        <button
                                                          onClick={() => {
                                                            setCurrentPage("choose-img");
                                                            setImage("");
                                                          }}
                                                          className="btn"
                                                        >
                                                          New Image
                                                        </button>
                                                      </>
                                                    ) : ""} */}
                                                </div>
                                              )}





                                            </div>
                                          </div>
                                        </td>
                                      </tr>
                                    ) : null}
                                    {/* </Accordion.Body> */}
                                    {/* </Accordion.Item> */}
                                    <tr>
                                      <td colSpan={"12"}>
                                        {customvalidated === "imgformat" ? (
                                          <span
                                            className="mt-2   text-center fs-6 text-danger"
                                            type="invalid"
                                          >
                                            Image Format should be in jpg,
                                            jpeg or png
                                          </span>
                                        ) : null}
                                      </td>
                                    </tr>
                                  </>
                                );
                              })}
                            {changeUnitproperty === "editvariety" ? (
                              <tr>
                                <td
                                  className="text-primary text-center mx-5"
                                  colSpan={12}
                                >
                                  Now You can edit vareity type
                                </td>
                              </tr>
                            ) : null}
                          </tbody>
                        </Table>
                        {/* </Accordion> */}
                        {/* </Col>
                      </Form.Group> */}

                      </div>
                    </div>
                  </div>
                  {/* </Form.Group> */}
                </div>



              </Modal.Body>

              <Modal.Footer>
                <button
                  className="button main_outline_button"
                  onClick={handlevarietyClose}
                >
                  Close
                </button>
              </Modal.Footer>
            </Form>
          </Modal>

          <DataTable
            columns={columns}
            data={pdata.results}
            pagination
            highlightOnHover
            pointerOnHover
            className={"table_body product_table"}
          />

          <SAlert
            show={VerityAlert}
            title="Product Name"
            text="Are you Sure you want to delete"
            onConfirm={hideAlert}
            showCancelButton={true}
            onCancel={closeAlert}
          />

          <SAlert
            show={Alert}
            title="Product Name"
            text="Are you Sure you want to delete"
            onConfirm={deleteProductAlert}
            showCancelButton={true}
            onCancel={closeAlert}
          />

          <SAlert
            show={ProductAlert}
            title="Added Successfully"
            text={"Product Added"}
            onConfirm={closeProductAlert}
          />

          <SAlert
            show={ProductDraftAlert}
            title="Added Successfully "
            text=" Product Added To Draft"
            onConfirm={closeProductAlert}
          />

          <SAlert
            show={UpdatetAlert}
            title="Updated Successfully "
            text=" Product Updated"
            onConfirm={closeProductAlert}
          />

          {/* feature product modal */}

          <Modal show={featureshow} onHide={featureModalClose}>
            <Form className="" novalidate validated={validated} ref={formRef} onSubmit={OnOfferProductAdd}>
              <Modal.Header closeButton>
                <Modal.Title>Add Offer Product</Modal.Title>
              </Modal.Header>
              {error === false ? (
                <p
                  className="mt-2 ms-2 text-danger text-center fs-6"
                  type="invalid"
                >
                  Already Added In Offred Product List!!!
                </p>
              ) : null}

              <Modal.Body className="p-3">
                <div className="d-flex justify-content-center align-items-center p-0 m-0">
                  <div className="">
                    <div className="">
                      <div className="row px-3">
                        <div className="col-12">
                          <Form.Group
                            className="mb-3"
                            controlId="formPlaintextName"
                          >
                            <Form.Label className="" column sm="12">
                              Product Name
                            </Form.Label>
                            <Form.Control
                              required
                              type="text"
                              placeholder="Name"
                              value={productname}
                              name={"productname"}

                            />
                          </Form.Group>
                        </div>
                        <div className="col-12">
                          <Form.Group
                            className="mb-3"
                            controlId="formPlaintextName"
                          >
                            <Form.Label className="" column sm="12">
                              Product Id
                            </Form.Label>
                            <Form.Control
                              type="text"
                              placeholder="Name"
                              value={featuredata.product_id}
                              name={"product_id"}
                              required
                            />
                          </Form.Group>
                        </div>
                        <div className="col-12">
                          <Form.Group
                            className="mb-3"
                            controlId="formPlaintextName"
                          >
                            <Form.Label className="" column sm="12">
                              Offer Type
                            </Form.Label>
                            <Form.Control
                              type="text"
                              placeholder="Name"
                              value={featuredata.fetured_type}
                              name={"fetured_type"}
                              required
                            />
                          </Form.Group>
                        </div>
                        <div className="col-12">
                          <Form.Group
                            className="mb-3"
                            controlId="formPlaintextName"
                          >
                            <Form.Label className="" column sm="12">
                              Start Date
                            </Form.Label>
                            <Form.Control
                              type="date"
                              placeholder="Name"
                              onChange={(e) => OnFeatureDateChaneg(e)}
                              value={featuredata.start_date}
                              name={"start_date"}
                              required
                              min={moment().format("YYYY-MM-DD")}
                            />
                            <Form.Control.Feedback
                              type="invalid"
                              className="h6"
                            >
                              Please fill start date
                            </Form.Control.Feedback>
                          </Form.Group>
                        </div>
                        <div className="col-12">
                          <Form.Group
                            className="mb-3"
                            controlId="formPlaintextName"
                          >
                            <Form.Label className="" column sm="12">
                              End Date
                            </Form.Label>
                            <Form.Control
                              type="date"
                              placeholder="Name"
                              onChange={(e) => OnFeatureDateChaneg(e)}
                              value={featuredata.end_date}
                              disabled={featuredata.start_date ? false : true}
                              name={"end_date"}
                              required
                              min={moment(featuredata.start_date).format(
                                "YYYY-MM-DD"
                              )}
                            />
                            <Form.Control.Feedback
                              type="invalid"
                              className="h6"
                            >
                              Please fill end date
                            </Form.Control.Feedback>
                          </Form.Group>
                        </div>
                      </div>
                    </div>
                  </div>
                </div>
              </Modal.Body>
              <Modal.Footer className="">
                <Iconbutton
                  type={"button"}
                  btntext={"Cancel"}
                  onClick={featureModalClose}
                  btnclass={"button main_outline_button "}
                />
                <Iconbutton
                  // onClick={OnOfferProductAdd}
                  btntext={"Added"}
                  btnclass={"button main_button "}
                />
              </Modal.Footer>
            </Form>
          </Modal>
          <SAlert
            show={RestoreAlert}
            title={productname}
            onConfirm={() => setRestoreAlert(false)}
          />
        </div>
      </div>
    </>
  );
}

export default Product;
