import React, { useEffect, useState } from "react";
// import Input from "./common/input";
// import { FaFileInvoiceDollar } from "react-icons/fa";
import DataTable from "react-data-table-component";
import MainButton from "./common/button";
import Form from "react-bootstrap/Form";
import { useNavigate } from "react-router-dom";
import "../../style/order.css";
// import OrderJson from "./json/orders";
import axios from "axios";
import moment from "moment";
// import Status from "./json/Status";
import Loader from "./common/loader";

function Orders() {
  let token = localStorage.getItem("token");
  const navigate = useNavigate();
  const [orderdata, setorderdata] = useState([]);
  const [changstatus, setchangstatus] = useState("");
  const [apicall, setapicall] = useState(false);
  const [loading, setLoading] = useState(false);
  const [searchdata, setsearchData] = useState({
    status: "",
    created_on: "",
  });

  // On selete the status or order limit to search :-
  const OnSearchChange = (e) => {
    setsearchData({ ...searchdata, [e.target.name]: e.target.value });
  };

  //To search by status and order limit :-
  const onSearchClick = () => {
    setapicall(true);
  };

  //To reset the search feild blank :-
  const OnReset = () => {
    setsearchData({ status: "", created_on: "" });
    setapicall(true);
  };

  //To get the order list :-
  useEffect(() => {
    setLoading(true);
    axios
      .post(
        `${process.env.REACT_APP_BASEURL_0}/orders_list`,
        {
          status: searchdata.status,
          created_on: searchdata.created_on,
        },
        {
          headers: {
            admin_token: token,
          },
        }
      )
      .then((response) => {

        setorderdata(response.data);
        setapicall(false);
        setLoading(false);
      })
      .catch(function (error) {
        console.log(error);
      });
  }, [apicall, changstatus]);

  const onStatusChange = (e, id, user_id) => {
    // e.prevantDefault();
    setchangstatus(e.target.value);
    setLoading(true);
    axios
      .put(
        `${process.env.REACT_APP_BASEURL_0}/order_status_change`,
        {
          status_change: e.target.value,
          id: id,
          user_id: user_id,
        },
        {
          headers: {
            admin_token: token,
          },
        }
      )
      .then((response) => {
        setLoading(false);
        setapicall(true);
      })
      .catch(function (error) {
        console.log(error);
        setLoading(false);
      });
  };

  // To go on the order details page :-
  const onOrderClick = (id) => {
    localStorage.setItem("orderid", id[0]);
    localStorage.setItem("userid", id[1]);

    navigate("/order_detail");
  };

  // Table data :-
  const columns = [
    {
      name: "Order Id",
      selector: (row) => (
        <p onClick={onOrderClick.bind(this, [row.id, row.user_id])}>
          {" "}
          {row.id}
        </p>
      ),
      sortable: true,
    },

    {
      name: "Quantity",
      selector: (row) => <p className="m-0">{row.total_quantity}</p>,
      sortable: true,
    },
    {
      name: "Tax",
      selector: (row) => (
        <p className="m-0">{Number(row.total_gst).toFixed(2)}</p>
      ),
      sortable: true,
    },

    {
      name: "Total Ammount",
      selector: (row) => (
        <p className="m-0">₹{Number(row.total_amount).toFixed(2)}</p>
      ),
      sortable: true,
    },
    {
      name: "Order Date",
      selector: (row) => moment(row.created_on).format("YYYY-MM-DD"),
      sortable: true,
    },
    {
      name: "Delivery Date",
      selector: (row) => row.delivery_date,
      sortable: true,
    },

    {
      name: "Pyament Mode",
      selector: (row) => row.payment_mode,
      sortable: true,
    },
    {
      name: "Status",
      selector: (row) => (
        <span
          className={
            row.status === "placed"
              ? "badge bg-warning"
              : row.status === "pending"
                ? "badge bg-secondary"
                : row.status === "shipped"
                  ? "badge bg-primary"
                  : row.status === "delivered"
                    ? "badge bg-success"
                    : row.status === "packed"
                      ? "badge bg-primary"
                      : row.status === "cancel"
                        ? "badge bg-danger"
                        : row.status === "approved"
                          ? "badge bg-info"
                          : "badge bg-dark"
          }
        >
          {row.status === "placed"
            ? "placed"
            : row.status === "delivered"
              ? "delivered"
              : row.status === "shipped"
                ? "shipped"
                : row.status === "packed"
                  ? "packed"
                  : row.status === "cancel"
                    ? "cancel"
                    : row.status === "approved"
                      ? "approved"
                      : row.status === "pending"
                        ? "pending"
                        : "return"}
        </span>
      ),
      sortable: true,
    },
    {
      name: "Change Status",
      selector: (row) => (
        <Form.Select
          aria-label="Search by delivery"
          size="sm"
          className="w-100"
          onChange={(e) => onStatusChange(e, row.id, row.user_id)}
          name="status"
        // value={row.product_status}
        >
          <option
            value="placed"
            selected={row.status === "placed" ? true : false}
          >
            Placed
          </option>
          <option
            value="pending"
            selected={row.status === "pending" ? true : false}
          >
            Pending
          </option>
          <option
            value="shipped"
            selected={row.status === "shipped" ? true : false}
          >
            Shipped
          </option>
          <option
            value="delivered"
            selected={row.status === "delivered" ? true : false}
          >
            Delivered
          </option>
          <option
            value="packed"
            selected={row.status === "packed" ? true : false}
          >
            Packed
          </option>
          <option
            value="cancel"
            selected={row.status === "cancel" ? true : false}
          >
            Cancel
          </option>
          <option
            value="approved"
            selected={row.status === "approved" ? true : false}
          >
            Approved{" "}
          </option>
          <option
            value="return"
            selected={row.status === "return" ? true : false}
          >
            Return{" "}
          </option>
        </Form.Select>
      ),
      sortable: true,
    },
  ];
  return (
    <>
      {loading === true ? <Loader /> : null}
      <div className="App">
        <h2>Orders</h2>
        <div className="card mt-3 px-3 ">
          <div className="product_page_searchbox bg-gray my-4">
            <div className="row">
              <div className="col-md-3 col-sm-6">
                <Form.Select
                  aria-label="Search by delivery"
                  className="adminselectbox"
                  onChange={OnSearchChange}
                  name="status"
                  value={String(searchdata.status)}
                >
                  <option value="">Delivery status</option>
                  <option value="delivered">Delivered</option>
                  <option value="pending">Pending</option>
                  <option value="approved">Approved</option>
                  <option value="packed">Packed</option>
                  <option value="placed">Placed</option>
                  <option value="return">Return</option>
                  <option value="cancel">Cancel</option>
                </Form.Select>
              </div>
              <div className="col-md-3 col-sm-6">
                <Form.Select
                  aria-label="Search by delivery_status"
                  className="adminselectbox"
                  onChange={OnSearchChange}
                  name="created_on"
                  value={String(searchdata.created_on)}
                >
                  <option value="">Order limits</option>
                  <option value="one">Today</option>
                  <option value="1">Yesterday</option>
                  <option value="15">Last 15 days orders</option>
                  <option value="30">Last 30 days orders</option>
                  <option value="90">Last 3 month orders</option>
                  <option value="180">Last 6 month orders</option>
                </Form.Select>
              </div>
              <div className="col-md-3 col-sm-6 aos_input ">
                <MainButton
                  btntext={"Search"}
                  onClick={onSearchClick}
                  btnclass={"button main_button w-100"}
                />
              </div>
              <div className="col-md-3 col-sm-6 aos_input ">
                <MainButton
                  btntext={"Reset"}
                  onClick={OnReset}
                  btnclass={"button main_button w-100"}
                />
              </div>
            </div>
          </div>

          <DataTable
            columns={columns}
            data={orderdata}
            pagination
            highlightOnHover
            pointerOnHover
            className={"table_body order_table"}
          />
        </div>
      </div>
    </>
  );
}

export default Orders;
